mapper.select:
SELECT *,DATE_FORMAT(c.tddata,'%Y-%m-%d %H:%i:%S') as ypsj,DATE_FORMAT(a.chudate,'%Y-%m-%d %H:%i:%S')  as chudate02 from
wf_jianli a ,wf_zps b , wf_tdjl c, wf_qtyh d where  c.tdzpsid = b.zpsid  and d.qtyhid = a.qtryid and a.qtryid = c.tdrid


        and (c.msjg = 1 or c.msjg =999)


        and c.msjg =#{msjg}



    and  a.xueli = #{tdid02}


    and c.tdid=#{tdid}


    and c.msjg = #{tdstatic}

SELECT *,DATE_FORMAT(c.tddata,'%Y-%m-%d %H:%i:%S') as ypsj,DATE_FORMAT(a.chudate,'%Y-%m-%d %H:%i:%S')  as chudate02 from
wf_jianli a ,wf_zps b , wf_tdjl c, wf_qtyh d where  c.tdzpsid = b.zpsid  and d.qtyhid = a.qtryid and a.qtryid = c.tdrid

    and  a.xueli = #{tdid02}


    and c.tdid=#{tdid}


    and c.tdstatic = #{tdstatic}

SELECT a.*,DATE_FORMAT(djsj,'%Y-%m-%d %H:%i:%S') as djshijian02 ,b.* from wf_zps a , wf_tdjl b where a.zpzt = 0  and a.zpsid = b.tdzpsid

    and  b.tdrid = #{tdrid}


    and a.zpsid = #{id}

SELECT * from wf_qtyh where 1=1

    and shoujihao =#{shoujihao}


    and mima = #{mima}

  and yhstatic=0

SELECT a.*,b.bmid,b.bmname,DATE_FORMAT(a.djshijian,'%Y-%m-%d %H:%i:%S') as djshijian02,DATE_FORMAT(a.yonggongriqi,'%Y-%m-%d %H:%i:%S') as yonggongriqi02 from wf_zhaopinxuqiu a,wfdepartment b where xqzt = 0 and b.bmid=a.xuqiubmid
and b.bmid = (SELECT rybmid from wfstafftable where ryid=#{a1} )
and a.xuqiurenshu >= #{xqrs001}

SELECT a.*,b.bmid,b.bmname,DATE_FORMAT(a.djshijian,'%Y-%m-%d %H:%i:%S') as djshijian02,DATE_FORMAT(a.yonggongriqi,'%Y-%m-%d %H:%i:%S') as yonggongriqi02
from wf_zhaopinxuqiu a,wfdepartment b where xqzt = 0 and b.bmid=a.xuqiubmid

    and a.xuqiurenshu  >=  #{id}

SELECT * from wfstafftable a, wfdepartment b where 1 = 1

 and   a.rybmid = b.bmid
 and a.ryid = #{ryid}

SELECT a.*,b.jsname,DATE_FORMAT(a.djsj,'%Y-%m-%d %H:%i:%S') as djsj01 from wf_zps a, wfjsb b where 1=1 and b.jsid = a.zhiwei
and a.zdry =#{id}
and a.tgxinzi02 <= #{id02} and #{id02} <= a.tgxinzi

mapper.update:
update wf_zps set

    skzt = #{skzt}


    ,shrid =#{shrid}


    ,skyj =#{skyj}


    ,shrname=#{shrname}

 where 1 = 1
and zpsid = #{zpsid}

mapper.select:
SELECT * from wf_zps where 1=1

    and skzt = #{id}


    and  tgxinzi02  <=  #{id02} and #{id02}   <=  tgxinzi
